export default function handleResponseFromAPI(promise) {
    return new Promise((resolve, reject) => {
      promise
        .then((result) => {
          console.log(result.data); // Log the data when the promise is resolved
          resolve(result); // Resolve the outer promise with the result
        })
        .catch((error) => {
          console.error(error); // Log the error when the promise is rejected
          reject(error); // Reject the outer promise with the error
        });
    });
  }
